	[[module css]]

/* =========================

   My (mysticgalaxy's) theme by me

   ========================= */



/* GLOBAL MIDNIGHT BACKGROUND */

html,

body {

    background-color: #05080f !important;

    color: #d6e3ff;

}



#content-wrap,

#main-content,

#page-content,

#container,

#header,

#top-bar {

    background-color: transparent !important;

}



#side-bar {

    background-color: #070b14 !important;

}



/* =========================

   PAGE CARD

   ========================= */

.page {

    display: block;

    overflow: hidden;

    font-family: "Monotype Corsiva", "Bradley Hand ITC", sans-serif;



    background-attachment: scroll;

    background-clip: border-box;

    background-origin: padding-box;

    background-position: 0px 8px;

    background-repeat: repeat;

    background-size: 100% 20px;



    background-color: #0b1220;

    background-image: linear-gradient(

        to top,

        #05080f 0%,

        #0b1220 40%,

        #121a2e 100%

    );



    border: 1px solid #1f2c4f;

    border-radius: 10px;

    padding: 10px;

    margin-bottom: 10px;



    box-shadow: 0px 4px 12px rgba(0,0,0,0.65);

    color: #d6e3ff;

}



.page p,

.page ul {

    line-height: 20px;

    margin: 0;

}

[[/module]]



[[module CSS]]

/* =========================

   COLLAPSIBLES / FORMS

   ========================= */

.fakeprot .mailform-box .buttons { display: none; }



.fakeprot + .collapsible-block .collapsible-block-link {

    padding: 0.1em 0.5em;

    text-decoration: none;

    background-color: #0f172a;

    border: 1px solid #1f2c4f;

    color: #9fc1ff;

}



.fakeprot + .collapsible-block .collapsible-block-link:hover,

.fakeprot + .collapsible-block .collapsible-block-link:active {

    background-color: #1a2440;

    color: #d6e3ff;

}



.fakeprot + .collapsible-block .collapsible-block-unfolded-link,

.fakeprot + .collapsible-block .collapsible-block-folded {

    margin: 0.5em 0;

    text-align: center;

}



.fakeprot .passw input[type=text] {

    text-security: disc;

    -webkit-text-security: disc;

    -mox-text-security: disc;

}



.mailform-box td:first-child {

    width: 80px;

}

[[/module]]



